import React,{useState} from "react";
import "./Login.css"


const Login = () => {

  const [currentFName, setFName] = useState("");
  const [currentLName, setLName] = useState("");

  function handleFirstNameInput(event){
    setFName(event.target.value);
    console.log(currentFName);
  };

  function handleLastNameInput(event){
    setLName(event.target.value);
    console.log(currentLName);
  };

return (
  <div className='main_login_box'>
  <div className='login_hello'>
      <h3>Hey There! Login here for an Epic Journey!</h3>
    <form className="login_controls">
        <div>
         <label> Enter Your First Name </label>
         <input type='text' placeholder='Enter First name' onChange={handleFirstNameInput}/>
        </div>
        <div>
         <label> Enter Your Last Name </label>
         <input type='text' placeholder='Enter Last name' onChange={handleLastNameInput}/>
        </div>
      </form>
    </div>
      <button type='submit'>Login</button>
    </div>
  );
}

export default Login;
